import React, { useState, useContext } from "react";
import './booking.css'
import { Form, FormGroup, ListGroupItem, Button } from 'reactstrap'
import { AuthContext } from "../../context/AuthContext";

import { useNavigate } from "react-router-dom"
import { BASE_URL } from "../../utils/config";




const Booking = ({ tour, avgRating }) => {

    const { price, reviews, title } = tour


    const { user } = useContext(AuthContext)



    const [booking, setBooking] = useState({
        userId: user && user._id,
        userEmail: user && user.email,
        tourName: title,
        fullName: " ",
        phone: " ",
        guestSize: 1,
        bookAt: " "

    })


    const handleChange = e => {
        setBooking(prev => ({ ...prev, [e.target.id]: e.target.value }))





    };


    const serviceFee = 10;
    const totalAmount = Number(price) * Number(booking.guestSize) + Number(serviceFee)


    //send data to the server
    const navigate = useNavigate()
    const handleClick = async e => {
        e.preventDefault()
        console.log(booking)
        try {
            if (!user || user === undefined || user === null) {
                return alert('Please Sign In')
            }

            const res = await fetch(`${BASE_URL}/booking`, {
                method: 'post',
                headers: {
                    'content-type': 'application/json'
                },
                booking: 'include',
                body: JSON.stringify(booking)
            })

            const result = await res.json()
            if (!res.ok) {
                return alert(result.message)
            }
            // navigate('/thank-you')
            navigate('/payment')

        } catch (error) {
            alert(error.message)

        }

        console.log(booking);
        
        navigate('/payment');
    }

    



    return <div className="booking">
        <div className="booking__top d-flex align-items-center
        justify-content-between">
            <h3>${price} <span>/per person</span></h3>
            <span className="tour__rating d-flex align-items-center">
                <i class="ri-star-fill" ></i>
                {avgRating === 0 ? null : avgRating} ({reviews?.length})

            </span>

        </div>

        {/* booking form */}
        <div className="booking__form">
            <h5>Information</h5>

            <Form className="booking__info-form" onSubmit={handleClick}>

                

                <FormGroup>
                      Rafting<input type="checkbox" onChange={handleChange} className="checkboxes"  />
                
                
                      Trekking<input type="checkbox" onChange={handleChange} className="checkboxes"  />
                
                
                      Skydiving<input type="checkbox" onChange={handleChange} className="checkboxes"  />
               
                
                      Cruise<input type="checkbox" onChange={handleChange} className="checkboxes"  />
                
                
                      Extreme_sports<input type="checkbox" onChange={handleChange} className="checkboxes"  />
                </FormGroup>



                <FormGroup>
                    <input type="text" placeholder="Full name" id="fullName"
                        required onChange={handleChange} />
                </FormGroup>
                <FormGroup>
                    <input type="number" placeholder="Phone" id="phone"
                        required onChange={handleChange} />
                </FormGroup>
                <FormGroup className="d-flex align-items-center gap-3">
                    <input type="date" placeholder=" " id="bookAt"
                        required onChange={handleChange} />
                    <input type="number" placeholder="Guest " id="guestSize"
                        required onChange={handleChange} />
                </FormGroup>

            </Form>
        </div>

        {/* booking form ends */}

        {/* booking bottom */}
        <div className="booking__bottom">
            <ListGroupItem className="border-0 px-0 ">
                <h5 className="d-flex align-items-center
                gap-1">${price} <i class="ri-close-line"></i> 1 person</h5>
                <span>${price}</span>

            </ListGroupItem>
            <ListGroupItem className="border-0 px-0 ">
                <h5>Service charges</h5>
                <span>${serviceFee}</span>

            </ListGroupItem>
            <ListGroupItem className="border-0 px-0 total ">
                <h5>Total</h5>
                <span>${totalAmount}</span>

            </ListGroupItem>

            <Button className="btn primary__btn w-100 mt-4" onClick={handleClick}>
                Book Now
            </Button>
        </div>

    </div>
};
export default Booking;
